        $(".authorPicture").css("background-image", "url('" + posts[0].author.photoUrl + "')");
        $("#namaPenulis").text(posts[0].author.name);
        $("#AuthorPictureInImg").attr("src",posts[0].author.photoUrl);
        $("#AuthorPictureInImg").attr("alt",posts[0].author.name);

