                    $("#keteranganPenulis").text(i.about);
                }
            }
        });

        $("#CommentBlockContainer").toggleClass("d-none");
        $("#postPopularData").children().each(function (i, n) {
            var r = fetch(n, true);
            $("#wadahPopuler").append(popularFormatter(r.title, r.subTitle, r.text, r.link));
        });
    }else if (archive) {
        var dat = url.split("/");
