                                    '</div></div></p></div></div>');
        }


        $(".search-body-advance .card").attr("style","min-width:19rem");
        $(".titleArticle").text("Arsip Blog pada " + ((isNaN([dat[dat.length - 3] - 1])) ? dat[dat.length - 2] : (["Januari", "Februari", "Maret", "April", "Mei", "Juni", "Juli", "Agustus",
            "September", "Oktober", "November", "Desember"
        ][dat[dat.length - 2] - 1] + " " + dat[dat.length - 3])));

    } else {
              var i = url.split("search");
                  if(i.length > 1){
                    if(i[i.length-1].split("/label/").length > 1){
                       var dat = i[i.length-1].split("/label/");
                       var searchKey = dat[dat.length-1].split("%20");
